"use client"

class FrontierSounds {
  private sounds: { [key: string]: HTMLAudioElement } = {}
  private isEnabled = true
  private volume = 0.3
  private isInitialized = false

  constructor() {
    if (typeof window !== "undefined") {
      this.initializeSounds()
    }
  }

  private initializeSounds() {
    if (this.isInitialized) return

    try {
      // Create audio elements for frontier sound effects
      this.sounds = {
        "saloon-piano": this.createAudio("/sounds/saloon-piano.mp3"),
        "whiskey-pour": this.createAudio("/sounds/whiskey-pour.mp3"),
        "wooden-thunk": this.createAudio("/sounds/wooden-thunk.mp3"),
        "spittoon-ping": this.createAudio("/sounds/spittoon-ping.mp3"),
        "coin-drop": this.createAudio("/sounds/coin-drop.mp3"),
        "paper-rustle": this.createAudio("/sounds/paper-rustle.mp3"),
        "boot-steps": this.createAudio("/sounds/boot-steps.mp3"),
        "door-creak": this.createAudio("/sounds/door-creak.mp3"),
        "horse-whinny": this.createAudio("/sounds/horse-whinny.mp3"),
        gunshot: this.createAudio("/sounds/gunshot.mp3"),
        "bell-ring": this.createAudio("/sounds/bell-ring.mp3"),
        "crowd-cheer": this.createAudio("/sounds/crowd-cheer.mp3"),
      }

      this.isInitialized = true
    } catch (error) {
      console.log("Sound initialization failed:", error)
    }
  }

  private createAudio(src: string): HTMLAudioElement {
    const audio = new Audio()
    audio.volume = this.volume
    audio.preload = "none" // Load on demand for better performance

    // Fallback to data URL for missing audio files
    audio.src = src
    audio.onerror = () => {
      // Create a silent audio data URL as fallback
      audio.src =
        "data:audio/wav;base64,"
    }

    return audio
  }

  private playSound(soundName: string) {
    if (!this.isEnabled || !this.sounds[soundName]) return

    try {
      const audio = this.sounds[soundName]
      audio.currentTime = 0
      audio.play().catch(() => {
        // Silently fail if audio can't play
      })
    } catch (error) {
      // Silently fail
    }
  }

  // Public methods for specific frontier sounds
  public playSaloonPiano() {
    this.playSound("saloon-piano")
  }
  public playWhiskeyPour() {
    this.playSound("whiskey-pour")
  }
  public playWoodenThunk() {
    this.playSound("wooden-thunk")
  }
  public playSpittoonPing() {
    this.playSound("spittoon-ping")
  }
  public playCoinDrop() {
    this.playSound("coin-drop")
  }
  public playPaperRustle() {
    this.playSound("paper-rustle")
  }
  public playBootSteps() {
    this.playSound("boot-steps")
  }
  public playDoorCreak() {
    this.playSound("door-creak")
  }
  public playHorseWhinny() {
    this.playSound("horse-whinny")
  }
  public playGunshot() {
    this.playSound("gunshot")
  }
  public playBellRing() {
    this.playSound("bell-ring")
  }
  public playCrowdCheer() {
    this.playSound("crowd-cheer")
  }

  // Control methods
  public setVolume(volume: number) {
    this.volume = Math.max(0, Math.min(1, volume))
    Object.values(this.sounds).forEach((audio) => {
      audio.volume = this.volume
    })
  }

  public toggleSounds() {
    this.isEnabled = !this.isEnabled
    return this.isEnabled
  }

  public isSoundEnabled() {
    return this.isEnabled
  }
}

// Create singleton instance
const frontierSounds = new FrontierSounds()

// Custom hook for using frontier sounds
export function useFrontierSounds() {
  return {
    playSaloonPiano: () => frontierSounds.playSaloonPiano(),
    playWhiskeyPour: () => frontierSounds.playWhiskeyPour(),
    playWoodenThunk: () => frontierSounds.playWoodenThunk(),
    playSpittoonPing: () => frontierSounds.playSpittoonPing(),
    playCoinDrop: () => frontierSounds.playCoinDrop(),
    playPaperRustle: () => frontierSounds.playPaperRustle(),
    playBootSteps: () => frontierSounds.playBootSteps(),
    playDoorCreak: () => frontierSounds.playDoorCreak(),
    playHorseWhinny: () => frontierSounds.playHorseWhinny(),
    playGunshot: () => frontierSounds.playGunshot(),
    playBellRing: () => frontierSounds.playBellRing(),
    playCrowdCheer: () => frontierSounds.playCrowdCheer(),
    setVolume: (volume: number) => frontierSounds.setVolume(volume),
    toggleSounds: () => frontierSounds.toggleSounds(),
    isSoundEnabled: () => frontierSounds.isSoundEnabled(),
  }
}

export default frontierSounds
